import React from 'react';
import {connect} from "react-redux";

import classProvider from '../tools/classProvider';
import '../style/global.css';
import '../style/calculate.css';

const mapStateToProps = (state) => {
  return {
    appState: state.appState
  }
};

const Calculator = (props) => {

  const Results = () => {
    return (
      <div className={classProvider(props.appState.theme, 'calculateElements')}>
        <Addition/>
        <Subtraction/>
        <Multiplication/>
        <Division/>
        <Exponentiation/>
        <RootSquare/>
        <RootCubic/>
      </div>
    );
  };

  const Addition = () => {
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Addition</p>
        <p>
          <strong>
          </strong>
        </p>
      </div>
    );
  };
  const Subtraction = () => {
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Subtraction</p>
        <p>
          <strong>
          </strong>
        </p>
      </div>
    );
  };
  const Division = () => {
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Division</p>
        <p>
          <strong>
          </strong>
        </p>
      </div>
    );
  };
  const Multiplication = () => {
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Multiplication</p>
        <p>
          <strong>
          </strong>
        </p>
      </div>
    );
  };
  const Exponentiation = () => {
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Exponentiation</p>
        <p>
          <strong>
          </strong>
        </p>
      </div>
    );
  };
  const Cube = () => {
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Cube</p>
        <p>
          <strong>
          </strong>
        </p>
      </div>
    );
  };
  const RootSquare = () => {
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Square root</p>
        <p>
          <strong>
          </strong>
        </p>
      </div>
    );
  };
  const RootCubic = () => {
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p></p>
        <p>
          <strong>
          </strong>
        </p>
      </div>
    );
  };

  return(
    <div className={classProvider(props.appState.theme, 'calculate')}>
      <div className={classProvider(props.appState.theme, 'calculateContainer')}>
        <div className={classProvider(props.appState.theme, 'calculateElements')}>
          <input type='number' placeholder='numerical value'
            className={classProvider(props.appState.theme, 'calculateElement')}/>
          <input type='number' placeholder='numerical value'
                 className={classProvider(props.appState.theme, 'calculateElement')}/>
        </div>
        <Results/>
      </div>
    </div>
  );
};

export default connect(mapStateToProps)(Calculator);
