import React from 'react';
import {connect} from "react-redux";

import classProvider from '../../tools/classProvider';
import '../../style/global.css';

const mapStateToProps = (state) => {
  return {
    appState: state.appState
  }
};

const Technologies = (props) => {

  const frontend = [
    {name: 'React.js', url: 'https://reactjs.org/', icon: ''},
    {name: 'Redux.js', url: 'https://redux.js.org/', icon: ''},
    {name: 'GraphQL', url: 'https://graphql.org/', icon: ''},
    {name: 'Apollo-client', url: 'https://www.apollographql.com/docs/react/', icon: ''}
  ];
  const backend = [
    {name: 'Node.js', url: '', icon: ''},
    {name: 'Express.js', url: '', icon: ''},
    {name: 'Mongoose.js', url: '', icon: ''},
    {name: 'JWT', url: '', icon: ''},
    {name: 'Bcrypt', url: '', icon: ''},
    {name: 'GraphQL', url: 'https://graphql.org/', icon: ''},
    {name: 'Apollo-server', url: '', icon: ''}
  ];
  const data = [
    {name: 'MongoDB - Apollo', url: '', icon: ''},
    {name: 'HSL API', url: '', icon: ''}
  ];

  const Front = () => {
    return frontend.map((f) => {
      return (
        <div className={classProvider(props.appState.theme, 'technologyElement')}>
          <a href={f.url}>{f.name}</a>
        </div>
      );
    });
  };
  const Back = () => {
    return backend.map((b) => {
      return <a className={classProvider(props.appState.theme, 'technologyElement')}
        href={b.url}>{b.name}</a>
    });
  };
  const Data = () => {
    return data.map((d) => {
      return <a className={classProvider(props.appState.theme, 'technologyElement')}
        href={d.url}>{d.name}</a>
    });
  };
  return(
    <div className='app'>
      <div className='appContainer'>
        <div className='appElements'>
          <h4 className={classProvider(props.appState.theme, 'heading')}>Used technology stack</h4>
        </div>
        <div className='appElements'>
          <h4 className={classProvider(props.appState.theme, 'heading')}>Frontend</h4>
        </div>
        <Front/>
        <div className='appElements'>
          <h4 className={classProvider(props.appState.theme, 'heading')}>Backend</h4>
        </div>
        <Back/>
        <div className='appElements'>
          <h4 className={classProvider(props.appState.theme, 'heading')}>Database & API</h4>
        </div>
        <Data/>
      </div>
    </div>
  );
};

export default connect(mapStateToProps)(Technologies);
