import React from 'react';
import {connect} from "react-redux";

import {FaReact} from 'react-icons/'

import classProvider from '../../tools/classProvider';
import '../../style/global.css';
import '../../style/technologies.css';

const mapStateToProps = (state) => {
  return {
    appState: state.appState
  }
};

const Technologies = (props) => {

  const frontend = [
    {name: 'React.js', url: 'https://reactjs.org/', icon: ''},
    {name: 'Redux.js', url: 'https://redux.js.org/', icon: ''},
    {name: 'GraphQL', url: 'https://graphql.org/', icon: ''},
    {name: 'Apollo-client', url: 'https://www.apollographql.com/docs/react/', icon: ''}
  ];
  const backend = [
    {name: 'Node.js', url: 'https://nodejs.org/en/', icon: ''},
    {name: 'Express.js', url: 'https://expressjs.com/', icon: ''},
    {name: 'mongoose.js', url: 'https://mongoosejs.com/', icon: ''},
    {name: 'JWT', url: 'https://jwt.io/', icon: ''},
    {name: 'bcrypt', url: 'https://github.com/kelektiv/node.bcrypt.js', icon: ''},
    {name: 'GraphQL', url: 'https://graphql.org/', icon: ''},
    {name: 'Apollo-server', url: 'https://www.apollographql.com/docs/apollo-server/', icon: ''}
  ];
  const data = [
    {name: 'MongoDB - Atlas', url: 'https://www.mongodb.com/cloud/atlas', icon: ''},
    {name: 'HSL API', url: 'https://digitransit.fi/en/developers/apis/1-routing-api/', icon: ''}
  ];

  const Front = () => {
    return frontend.map((f) => {
      return <a className={classProvider(props.appState.theme, 'technologyElement')} href={f.url}>{f.name}</a>
    });
  };
  const Back = () => {
    return backend.map((b) => {
      return <a className={classProvider(props.appState.theme, 'technologyElement')} href={b.url}>{b.name}</a>
    });
  };
  const Data = () => {
    return data.map((d) => {
      return <a className={classProvider(props.appState.theme, 'technologyElement')} href={d.url}>{d.name}</a>
    });
  };
  return(
    <div className='app'>
      <div className='appContainer'>
        <div className='appElements'>
          <h4 className={classProvider(props.appState.theme, 'heading')}>Used technology stack</h4>
        </div>
        <div className='appElements'>
          <h4 className={classProvider(props.appState.theme, 'heading')}>Frontend</h4>
        </div>
        <>
          <Front/>
        </>
        <div className='appElements'>
          <h4 className={classProvider(props.appState.theme, 'heading')}>Backend</h4>
        </div>
        <Back/>
        <div className='appElements'>
          <h4 className={classProvider(props.appState.theme, 'heading')}>Database & API</h4>
        </div>
        <Data/>
      </div>
    </div>
  );
};

export default connect(mapStateToProps)(Technologies);
