import React from 'react';
import {connect} from "react-redux";
import {Link} from 'react-router-dom';
import '../style/navigation.css';
import '../style/global.css';

const mapStateToProps = (state) => {
  return {
    appState: state.appState,
    loginState: state.loginState
  }
};

const classNames = (theme, element) => {
  if (theme === 'dev') {
    switch(element) {
      case 'navMenu':
        return 'navMenuDe';
      case 'navButton':
        return 'navButtonDe';
      case 'navContent':
        return 'navContentDe';
      case 'heading':
        return 'headingDe';
      default:
        return '';
    }
  } else if (theme === 'dark') {
    switch(element) {
      case 'navMenu':
        return 'navMenuDa';
      case 'navButton':
        return 'navButtonDa';
      case 'navContent':
        return 'navContentDa';
      case 'heading':
        return 'headingDa';
      default:
        return '';
    }
  } else {
    switch(element) {
      case 'navMenu':
        return 'navMenuLi';
      case 'navButton':
        return 'navButtonLi';
      case 'navContent':
        return 'navContentLi';
      case 'heading':
        return 'headingLi';
      default:
        return '';
    }
  }
};

const Navigation = (props) => {

  const themes = ['light', 'dark', 'dev'];

  const Menu = () => {
    const theme = props.appState.theme;
    return (
      <div className={classNames(theme, 'navMenu')}>
        <button className={classNames(theme, 'navButton')}>
          Menu
        </button>
        <div className={classNames(theme, 'navContent')}>
          <Link to='/'>1</Link>
          <Link to='/calculate'>2</Link>
          <Link to='/dashboard'>3</Link>
        </div>
      </div>
    );
  };

  const ThemeSelector = () => {
    const theme = props.appState.theme;
    return (
      <div className={classNames(theme, 'navMenu')}>
        <button className={classNames(theme, 'navButton')}>
          <strong>{props.appState.theme}</strong> theme selected
        </button>
        <div className={classNames(theme, 'navContent')}>
          {themes.map((t) =>
            <a key={t} onClick={event => switchTheme(event, t)}>{t}</a>
          )}
        </div>
      </div>
    );
  };

  const LoginModule = () => {
    const theme = props.appState.theme;
    return (
      <div>
        <button>
          {props.loginState.user ? 'logout' : 'login'}
        </button>
        <div>
        </div>
      </div>
    );
  };

  function switchTheme(event, theme) {
    event.preventDefault();
    props.dispatch({type: 'switchTheme', theme: theme});
  }

  return(
    <div className='navRow'>
      <Menu/>
      <h3 className={classNames(props.appState.theme, 'heading')}>demo</h3>
      <ThemeSelector/>
    </div>
  )
};

export default connect(mapStateToProps)(Navigation);
