import React, {useState} from 'react';
import {connect} from "react-redux";
import {evaluate} from "mathjs";

import classProvider from '../tools/classProvider';
import '../style/global.css';
import '../style/calculate.css';

const mapStateToProps = (state) => {
  return {
    appState: state.appState
  }
};

const Calculator = (props) => {
  const [value1, setValue1] = useState(0);
  const [value2, setValue2] = useState(0);

  const Results = () => {
    return (
      <div className={classProvider(props.appState.theme, 'calculateElements')}>
        <Addition/>
        <Subtraction/>
        <Multiplication/>
        <Division/>
        <Exponentiation/>
        <Cube/>
        <RootSquare/>
        <RootCubic/>
      </div>
    );
  };

  const Addition = () => {
    const result = Number(value1) + Number(value2);
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Addition</p>
        <p>{value1} + {value2} = <strong>{result}</strong></p>
      </div>
    );
  };
  const Subtraction = () => {
    const result = Number(value1) - Number(value2);
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Subtraction</p>
        <p>{value1} - {value2} = <strong>{result}</strong></p>
      </div>
    );
  };
  const Division = () => {
    if (Number(value2) === 0) {
      return (
        <div className={classProvider(props.appState.theme, 'calculateResult')}>
          <p>Division</p>
          <a href='https://en.wikipedia.org/wiki/Division_by_zero'> nope </a>
        </div>
      );
    } else {
      const result = Number(value1) / Number(value2);
      return (
        <div className={classProvider(props.appState.theme, 'calculateResult')}>
          <p>Division</p>
          <p>{value1} / {value2} = <strong>{result}</strong></p>
        </div>
      );
    }
  };
  const Multiplication = () => {
    const result = Number(value1) * Number(value2);
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Multiplication</p>
        <p>{value1} * {value2} = <strong>{result}</strong></p>
      </div>
    );
  };
  const Exponentiation = () => {
    let result = Number(value1) ** Number(value2);
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Exponentiation</p>
        <p>{value1} ^ {value2} = <strong>{result}</strong></p>
      </div>
    );
  };
  const Cube = () => {
    let result;
    if (value2 > 0) {
      result = Number(value1) ** 3;
      const res2 = Number(value2) ** 3;
      return (
        <div className={classProvider(props.appState.theme, 'calculateResult')}>
          <p>Cube</p>
          <p>{value1} ^ 3 = <strong>{result}</strong></p>
          <p>{value2} ^ 3 = <strong>{res2}</strong></p>
        </div>
      );
    } else {
      result = Number(value1) ** 3;
      return (
        <div className={classProvider(props.appState.theme, 'calculateResult')}>
          <p>Cube</p>
          <p>{value1} ^ 3 = <strong>{result}</strong></p>
        </div>
      );
    }
  };
  const RootSquare = () => {
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Square root</p>
        <p>
          <strong>
          </strong>
        </p>
      </div>
    );
  };
  const RootCubic = () => {
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Cube root</p>
        <p>
          <strong>
          </strong>
        </p>
      </div>
    );
  };

  return(
    <div className={classProvider(props.appState.theme, 'calculate')}>
      <div className={classProvider(props.appState.theme, 'calculateContainer')}>
        <div className={classProvider(props.appState.theme, 'calculateElements')}>
          <input type='number' placeholder='numerical value' onChange={({target}) => setValue1(target.value)}
            className={classProvider(props.appState.theme, 'calculateElement')}/>
          <input type='number' placeholder='numerical value' onChange={({target}) => setValue2(target.value)}
                 className={classProvider(props.appState.theme, 'calculateElement')}/>
        </div>
        <Results/>
      </div>
    </div>
  );
};

export default connect(mapStateToProps)(Calculator);
