import React, {useState} from 'react';
import {connect} from "react-redux";
import numbro from 'numbro';

import classProvider from '../tools/classProvider';
import '../style/global.css';
import '../style/calculate.css';

const mapStateToProps = (state) => {
  return {
    appState: state.appState
  }
};

const Calculator = (props) => {
  const [value1, setValue1] = useState(0);
  const [value2, setValue2] = useState(0);

  const Results = () => {
    return (
      <>
        <div className={classProvider(props.appState.theme, 'calculateElements')}>
          <Addition/>
          <Multiplication/>
          <Exponentiation/>
          <Cube/>
        </div>
        <div className={classProvider(props.appState.theme, 'calculateElements')}>
          <Subtraction/>
          <Division/>
          <RootSquare/>
          <RootCubic/>
        </div>
      </>
    );
  };

  const Addition = () => {
    const result = Number(value1) + Number(value2);
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Addition</p>
        <p>{value1} + {value2} = <strong>{result}</strong></p>
      </div>
    );
  };
  const Subtraction = () => {
    const result = numbro(Number(value1) - Number(value2)).format({thousandSeparated: true, trimMantissa: true, mantissa: 4});
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Subtraction</p>
        <p>{value1} - {value2} = <strong>{result}</strong></p>
      </div>
    );
  };
  const Division = () => {
    if (Number(value2) === 0) {
      return (
        <div className={classProvider(props.appState.theme, 'calculateResult')}>
          <p>Division</p>
          <a href='https://en.wikipedia.org/wiki/Division_by_zero'> nope </a>
        </div>
      );
    } else {
      const result = numbro(Number(value1) / Number(value2)).format({thousandSeparated: true, trimMantissa: true, mantissa: 4});
      return (
        <div className={classProvider(props.appState.theme, 'calculateResult')}>
          <p>Division</p>
          <p>{value1} / {value2} = <strong>{result}</strong></p>
        </div>
      );
    }
  };
  const Multiplication = () => {
    const result = numbro(Number(value1) * Number(value2)).format({thousandSeparated: true, trimMantissa: true, mantissa: 4});
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Multiplication</p>
        <p>{value1} * {value2} = <strong>{result}</strong></p>
      </div>
    );
  };
  const Exponentiation = () => {
    const res1 = Number(value1) ** 2;
    const res2 = Number(value2) ** 2;
    const res3 = Number(value1) ** Number(value2);
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Exponentiation</p>
        <p>{value1} ^ 2 = <strong>{res1}</strong></p>
        <p>{value2} ^ 2 = <strong>{res2}</strong></p>
        <p>{value1} ^ {value2} = <strong>{res3}</strong></p>
      </div>
    );
  };
  const Cube = () => {
    const res1 = Number(value1) ** 3;
    const res2 = Number(value2) ** 3;
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Cube</p>
        <p>{value1} ^ 3 = <strong>{res1}</strong></p>
        <p>{value2} ^ 3 = <strong>{res2}</strong></p>
      </div>
    );
  };
  const RootSquare = () => {
    const res1 = Math.sqrt(Number(value1));
    const res2 = Math.sqrt(Number(value2));
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Square root</p>
        <p>{'\u221a'}{value1} = <strong>{res1}</strong></p>
        <p>{'\u221a'}{value2} = <strong>{res2}</strong></p>
      </div>
    );
  };
  const RootCubic = () => {
    const res1 = Math.cbrt(Number(value1));
    const res2 = Math.cbrt(Number(value2));
    return (
      <div className={classProvider(props.appState.theme, 'calculateResult')}>
        <p>Cube root</p>
        <p>{'\u221B'}{value1} = <strong>{res1}</strong></p>
        <p>{'\u221B'}{value2} = <strong>{res2}</strong></p>
      </div>
    );
  };

  return(
    <div className={classProvider(props.appState.theme, 'calculate')}>
      <div className={classProvider(props.appState.theme, 'calculateContainer')}>
        <div className={classProvider(props.appState.theme, 'calculateElements')}>
          <input type='number' placeholder='numerical value' onChange={({target}) => setValue1(target.value)}
            className={classProvider(props.appState.theme, 'calculateElement')}/>
          <input type='number' placeholder='numerical value' onChange={({target}) => setValue2(target.value)}
                 className={classProvider(props.appState.theme, 'calculateElement')}/>
        </div>
        <Results/>
      </div>
    </div>
  );
};

export default connect(mapStateToProps)(Calculator);
